INSERT INTO `schedule` (`DETAIL`, `SUMARY`, `ID_TOUR`) VALUES
	('<p style="text-align: justify;"><span style="color: #2f4f4f;"><span style="font-size: 16px;"><strong><u>BUỔI TỐI</u></strong>: &nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\r\n<p><span style="color: #2f4f4f;"><span style="font-size: 16px;"><u><strong>20h00:</strong></u> Xe v&agrave;&nbsp;HDV Cty </span></span><span style="color: #ff8c00;"><span style="font-size: 16px;">Du Lịch Nam Thi&ecirc;n</span></span><span style="color: #2f4f4f;"><span style="font-size: 16px;">&nbsp;đ&oacute;n đo&agrave;n tại điểm hẹn, khởi h&agrave;nh đi&nbsp;Bạc Li&ecirc;u. Qu&yacute; kh&aacute;ch nghỉ đ&ecirc;m tr&ecirc;n xe với hệ thống xe đời mới m&aacute;y lạnh, đảm bảo qu&yacute; kh&aacute;ch sẽ c&oacute; một giấc ngủ ngon!.</span></span></p>\r\n<h2><span style="color: #ff0000;"><span style="font-size: 20px;">NG&Agrave;Y 01: THAM DỰ NG&Agrave;Y LỄ GIỖ CHA DIỆP &ndash; CH&Ugrave;A ĐẤT S&Eacute;T</span></span></h2>\r\n<p style="text-align: right;"><span style="font-size: 16px;"><strong><span style="color: #ff0000;">(Ăn S&aacute;ng, Trưa)</span></strong></span></p>\r\n<p style="text-align: justify;"><span style="color: #2f4f4f;"><span style="font-size: 16px;"><strong><u>04h30</u></strong>: Đo&agrave;n đến&nbsp;<strong>nh&agrave; thờ Cha Diệp</strong>,&nbsp;hay c&ograve;n gọi l&agrave;&nbsp;nh&agrave; thờ Tắc Sậy - nơi gắn liền với một nh&acirc;n vật nổi tiếng &ndash; cha Phanxico Xavi&ecirc; Trương Bửu Diệp, vị linh mục được xem như một vị th&aacute;nh v&igrave; sự linh thi&ecirc;ng, cho những ai tin tưởng nguyện cầu. Nơi đ&acirc;y kh&ocirc;ng chỉ l&agrave; điểm h&agrave;nh hương của người miền T&acirc;y m&agrave; c&ograve;n của những người Kit&ocirc; gi&aacute;o ở nhiều v&ugrave;ng miền kh&aacute;c. </span></span></p>\r\n<p style="text-align: center;"><img style="height: 539px; width: 700px;" src="http://namthientravel.com/remcua/upload/images/hanhhuong/tour-hanh-huong-2.jpg" alt="tour-hanh-huong-2" /></p>\r\n<p style="text-align: center;"><span style="color: #2f4f4f;"><span style="font-size: 16px;">Tham quan nh&agrave; thờ Cha Diệp</span></span></p>\r\n<p style="text-align: justify;"><span style="color: #2f4f4f;"><span style="font-size: 16px;">Nh&agrave; thờ hiện c&ograve;n l&agrave; Trung t&acirc;m Truyền gi&aacute;o Phanxic&ocirc; của Gi&aacute;o phận. Qu&yacute; kh&aacute;ch tham quan, viếng mộ Cha Phaxico Xavi&ecirc; Trương Bửu Diệp, tham gia th&aacute;nh lễ l&uacute;c 5h s&aacute;ng, cầu nguyện, xin ơn,&hellip; cảm nhận được kh&ocirc;ng kh&iacute; trang nghi&ecirc;m v&agrave; thi&ecirc;ng li&ecirc;ng của nơi n&agrave;y. Sau đ&oacute; đo&agrave;n&nbsp;d&ugrave;ng điểm t&acirc;m s&aacute;ng tại nh&agrave; h&agrave;ng.</span></span></p>\r\n<p style="text-align: center;"><span style="color: #2f4f4f;"><span style="font-size: 16px;"><img style="height: 464px; width: 700px;" src="http://namthientravel.com/remcua/upload/images/hanhhuong/tour-hanh-huong-vieng.jpg" alt="tour-hanh-huong-vieng" /></span></span></p>\r\n<p style="text-align: justify;"><span style="color: #2f4f4f;"><span style="font-size: 16px;"><u><strong>08h00</strong></u>:&nbsp;Đo&agrave;n d&ugrave;ng điểm t&acirc;m s&aacute;ng tại nh&agrave; h&agrave;ng. Sau đ&oacute; tiếp tục khởi h&agrave;nh tham quan <strong>Ch&ugrave;a Phật B&agrave; Nam Hải</strong>, c&ograve;n được gọi l&agrave; Mẹ Nam Hải hoặc Quan &Acirc;m Phật Đ&agrave;i, tọa lạc tr&ecirc;n diện t&iacute;ch khoảng 6ha, ở cửa biển Nh&agrave; M&aacute;t. Đ&acirc;y l&agrave; c&ocirc;ng tr&igrave;nh kiến tr&uacute;c &ndash; văn h&oacute;a &ndash; t&acirc;m linh nổi bật của Bạc Li&ecirc;u, hằng năm thu h&uacute;t h&agrave;ng ng&agrave;n lượt kh&aacute;ch đến h&agrave;nh hương, tham quan, du lịch...</span></span></p>\r\n<p style="text-align: center;"><img style="height: 394px; width: 700px;" src="http://namthientravel.com/remcua/upload/images/hanhhuong/phat-ba-nam-hai.jpg" alt="phat-ba-nam-hai" /></p>\r\n<p style="text-align: center;"><span style="color: #2f4f4f;"><span style="font-size: 16px;">Tham quan&nbsp;Quan &Acirc;m Phật Đ&agrave;i</span></span></p>\r\n<p>&nbsp;</p>', '', 1),
	('<p><span style="color: #555555; font-family: Arial,Helvetica Neue,Helvetica,sans-serif; font-size: small;"><span style="line-height: 22px;">05h00: Xe v&agrave; <strong>HDV NAM BỘ TRAVEL</strong> đ&oacute;n đo&agrave;n tại điểm hẹn&nbsp;</span></span></p>\r\n<p><span style="color: #555555; font-family: Arial,Helvetica Neue,Helvetica,sans-serif; font-size: small;"><span style="line-height: 22px;">05h30:Khởi h&agrave;nh đi Vũng T&agrave;u. Tr&ecirc;n đường đi HDV sẽ thuyết minh cho Qu&yacute; kh&aacute;ch t&igrave;m hiểu những danh lam, thắng cảnh, phong tục tập qu&aacute;n từngv&ugrave;ng m&agrave; đo&agrave;n đi qua v&agrave; tổ chức một số tr&ograve; chơi đầy vui nhộn v&agrave; hấp dẫn như:</span></span></p>\r\n<p><span style="color: #555555; font-family: Arial,Helvetica Neue,Helvetica,sans-serif; font-size: small;"><span style="line-height: 22px;">- &nbsp; &nbsp; &nbsp; &nbsp;Đi T&igrave;m Ẩn Số</span></span></p>\r\n<p><span style="color: #555555; font-family: Arial,Helvetica Neue,Helvetica,sans-serif; font-size: small;"><span style="line-height: 22px;">- &nbsp; &nbsp; &nbsp; &nbsp;Tri t&igrave;m b&aacute;u vật</span></span></p>\r\n<p><span style="color: #555555; font-family: Arial,Helvetica Neue,Helvetica,sans-serif; font-size: small;"><span style="line-height: 22px;">- &nbsp; &nbsp; &nbsp; &nbsp;H&aacute;t karaoke</span></span></p>\r\n<p><span style="color: #555555; font-family: Arial,Helvetica Neue,Helvetica,sans-serif; font-size: small;"><span style="line-height: 22px;">- &nbsp; &nbsp; &nbsp; &nbsp;Chiếc N&oacute;n Kỳ Diệu</span></span></p>\r\n<p><span style="color: #555555; font-family: Arial,Helvetica Neue,Helvetica,sans-serif; font-size: small;"><span style="line-height: 22px;">- &nbsp; &nbsp; &nbsp; &nbsp;B&eacute; Khỏe B&eacute; Ngoan,... với những phần qu&agrave; bất ngờ v&agrave; th&uacute; vị.</span></span></p>\r\n<p><span style="color: #555555; font-family: Arial,Helvetica Neue,Helvetica,sans-serif; font-size: small;"><span style="line-height: 22px;